mapper.insert:

    select BACS_MENU_SEQ.nextval from dual

insert into BACS_MENU
    (
        menuNum
        , menuName
        , menuPrice

            , menuImg


            , menuDesc

        , storeId
        , categoryNum
    )
values
    (
        #{menuNum}
        , #{menuName}
        , #{menuPrice}

            , #{menuImg}


            , #{menuDesc}

        , #{storeId}
        , #{categoryNum}
    )

insert into BACS_MENU_OPTION (OPTIONNUM, OPTIONNAME, OPTIONVALUE, OPTIONPRICE, MENUNUM) select BACS_MENU_OPTION_SEQ.nextval, A.* from (
select #{item.optionName} optionName , #{item.optionValue} optionValue , #{item.optionPrice} optionPrice , #{item.menuNum} menuNum from dual
) A

mapper.update:
update BACS_MENU
set menuName = #{menuName}
    , menuPrice = #{menuPrice}
    , menuImg = #{menuImg}
    , menuDesc = #{menuDesc}
    , categoryNum = #{categoryNum}
where menuNum = #{menuNum} and storeId = #{storeId}

update BACS_MENU_OPTION
set optionName = #{optionName}
  , optionValue = #{optionValue}
  , optionPrice = #{optionPrice}
where menuNum = #{menuNum} and optionNum = #{optionNum}
